	function softlab_fonts_url() {
		$fonts_url = '';
			
		/* Translators: If there are characters in your language that are not
		* supported by Nunito, translate this to 'off'. Do not translate
		* into your own language.
		*/
		$nunito = esc_html_x( 'on', 'Nunito font: on or off', 'softlab' );
		
		/* Translators: If there are characters in your language that are not
		* supported by Poppins, translate this to 'off'. Do not translate
		* into your own language.
		*/
		$poppins = esc_html_x( 'on', 'Poppins: on or off', 'softlab' );	

		/* Translators: If there are characters in your language that are not
		* supported by Playfair Display, translate this to 'off'. Do not translate
		* into your own language.
		*/
		$playfair_display = esc_html_x( 'on', 'Playfair Display: on or off', 'softlab' );
			
		
		if ( 'off' !== $nunito || 'off' !== $poppins || 'off' !== $playfair_display ) {
			$font_families = array();
				
			if ( 'off' !== $nunito ) {
				$font_families[] = 'Nunito:400,600,700';
			}
				
			if ( 'off' !== $poppins ) {
				$font_families[] = 'Poppins:700,500,600,400,500,600,700';
			}		

			if ( 'off' !== $playfair_display ) {
				$font_families[] = 'Playfair Display:700italic';
			}
				
			$query_args = array(
				'family' => urlencode( implode( '|', $font_families ) ),
				'subset' => urlencode( 'latin,latin-ext' ),
			);
				
			$fonts_url = add_query_arg( $query_args, 'https://fonts.googleapis.com/css' );
		}
		
		return esc_url_raw( $fonts_url );
	}

